select , , ,
f. TCC_ID,f.TCC_Total_Price,f.TCC_Dish_ID,fi.dish_Id,fi.dish_Name,fi.dish_price,si.table_Id,si.table_Name,si.table_state, se.employee_Id as Waiter_employee_Id,se.employee_Name as Waiter_employee_Name,ei.customer_Id,ei.customer_Name,ei.customer_Tel,ei .customer_Gender,ni.mem_ID,ni.mem_Name,ni.mem_User_Name,ni.mem_Tel,ni.mem_Gender from t_tempcarteinfo m
LEFT JOIN t_endCarteContent f ON m.TCI_ID=f.TCC_TCI_ID LEFT JOIN t_dish fi ON f.TCC_Dish_Id=fi.dish_id LEFT JOIN t_dinningTable si ON m.TCI_Table_ID=si.table_ID LEFT JOIN t_EmployeeInfo se ON m.TCI_Waiter_ID=se.employee_Id LEFT JOIN t_Customer ei ON m.TCI_Customer_ID=ei.Customer_ID LEFT JOIN t_MemberInfo ni ON m.TCI_Mem_ID=ni.Mem_ID where TCI_ID = #{tciId,jdbcType=INTEGER}
