mapper.update:
update t_dishtype
set dish_Type_Name = #{dishTypeName,jdbcType=VARCHAR},
  dish_Type_Desc = #{dishTypeDesc,jdbcType=VARCHAR},
  del_Flag = #{delFlag,jdbcType=TINYINT},
  subBy = #{subby,jdbcType=INTEGER}
where dish_Type_ID = #{dishTypeId,jdbcType=INTEGER}

mapper.select:
select dish_Type_ID, dish_Type_Name, dish_Type_Desc, del_Flag, last_SubTime, subBy
from t_dishtype
where dish_Type_ID = #{dishTypeId,jdbcType=INTEGER}
